import ConnectionGene from './connection_gene';
import { randInt, rand } from './helpers';
import NodeGene from './node_gene';

export default class Genome {
    constructor() {
        this.connections = [];
        this.nodes = [];
    }

    addConnectionMutation(globalInnovation) {
        let node1 = this.nodes[randInt(0, this.nodes.length - 1)];
        let node2 = this.nodes[randInt(0, this.nodes.length - 1)];
        let weight = rand(-1, 1);

        let reversed = false;
        if(node1.type == NodeGene.TYPE.HIDDEN && node2.type == NodeGene.TYPE.INPUT)
            reversed = true;
        else if(node1.type == NodeGene.TYPE.OUTPUT && node2.type == NodeGene.TYPE.HIDDEN)
            reversed = true;
        else if(node1.type == NodeGene.TYPE.OUTPUT && node2.type == NodeGene.TYPE.INPUT)
            reversed = true;

        let connectionImpossible = false;
        if(node1.type == NodeGene.TYPE.INPUT && node2.type == NodeGene.TYPE.INPUT)
            connectionImpossible = true;
        else if(node1.type == NodeGene.TYPE.OUTPUT && node2.type == NodeGene.TYPE.OUTPUT)
            connectionImpossible = true;
        else if(node1.id == node2.id)
            connectionImpossible = true;

        let connectionExists = false;
        for(let i=0; i<this.connections.length; ++i) {
            if(connections[i].inNode == node1.id && connections[i].outNode == node2.id) {
                connectionExists = true;
                break;
            }
            if(connections[i].inNode == node2.id && connections[i].outNode == node1.id) {
                connectionExists = true;
                break;
            }
        }

        if(connectionExists || connectionImpossible)
            return;

        let newConnection = new ConnectionGene(reversed ? node2.id : node1.id, reversed ? node1.id : node2.id, weight, true, globalInnovation.getInnovation());
        this.connections.push(newConnection);
    }

    addNodeMutation(globalInnovation) {
        let connection = this.connections[randInt(0, this.connections.length - 1)];
        let inNode = this.getNodeById(connection.inNode);
        let outNode = this.getNodeById(connection.outNode);

        connection.enabled = false;

        // TODO: check the setting of node id
        let newNode = new NodeGene(NodeGene.TYPE.HIDDEN, globalInnovation.getInnovation());
        let inToNew = new ConnectionGene(inNode.id, newNode.id, 1, true, globalInnovation.getInnovation());
        let newToOut = new ConnectionGene(newNode.id, outNode.id, connection.weight, true, globalInnovation.getInnovation());

        this.nodes.push(newNode);
        this.connections.push(inToNew);
        this.connections.push(newToOut);
    }

    // parent1 should be fitter than parent2
    crossover(parent1, parent2) {
        let child = new Genome();

        for(let i=0; i<parent1.nodes.length; ++i) {
            child.nodes.push(parent1.nodes[i].copy());
        }

        for(let i=0; i<parent1.connections.length; ++i) {
            let matchingConnection = parent2.getConnection(parent1.connections[i].innovation);
            if(matchingConnection !== null) {
                let childConnectionGene = Math.random() >= 0.5 ? parent1.connections[i].copy() : matchingConnection.copy();
                child.connections.push(childConnectionGene);
            }
            else {
                let childConnectionGene = parent1.connections[i].copy();
                child.connections.push(childConnectionGene);
            }
        }

        return child;
    }

    getNodeById(id) {
        return this.nodes.find(node => node.id === id);
    }

    getConnection(innovation) {
        let connection = this.connections.find(connection => connection.innovation === innovation);
        if(connection === undefined)
            return null;
        return connection;
    }
};
